import React, { useState, useEffect } from "react";
import { AppBar, Toolbar, Typography } from "@mui/material";
import { Link, useLocation } from "react-router-dom";
import "./styles.css";

/**
 * Define TopBar, a React component of Project 4.
 */
function TopBar() {
  const location = useLocation();
  const {userName, setUserName} = useState("");
  const pathParts = location.pathname.split("/");
  const currentView = pathParts[pathParts.length - 2];

  let title = "PhotoShare";

  if (currentView === "users") {
    const userId = pathParts[pathParts.length - 1];
    useEffect(() => {
      const fetchData = async () => {
        try{
          const user = await axios(
            `http://localhost:8081/api/user/${userId}`, {withCredentials: true}
          );
          setUserName(user.data.firstname+user.data.lastname);
          setLoggedIn(true);
        }catch(er){
          console.log(er);
        }
      };
        fetchData();
    },[]);
    title = `User Details: ${userName}`;
  } else if (currentView === "photos") {
    const userId = pathParts[pathParts.length - 1];
    title = `Photos of ${userId}`;
  }

  if (!sessionStorage.getItem("isLoggedIn")) {
    return (
      <AppBar className="topbar-appBar" position="absolute">
        <Toolbar>
          <Typography
            variant="h6"
            to="/"
            color="inherit"
            style={{ textDecoration: "none" }}
          >
            Photo Sharing App
          </Typography>
          <Typography
            variant="h6"
            component="div"
            sx={{ flexGrow: 1 }}
          ></Typography>
          <Typography variant="h6" component="div">
            Please Login!
          </Typography>
        </Toolbar>
      </AppBar>
    );
  }
  return (
    <AppBar className="topbar-appBar" position="absolute">
      <Toolbar>
        <Typography
          variant="h6"
          component={Link}
          to="/"
          color="inherit"
          style={{ textDecoration: "none" }}
        >{title}</Typography>
        <Typography
          variant="h6"
          component="div"
          sx={{ flexGrow: 1 }}
        >{userName}</Typography>
        <Typography variant="h6" component="div">
          {}
        </Typography>
      </Toolbar>
    </AppBar>
  );
}

export default TopBar;
