import { Button } from "@mui/material"
import { useState } from "react"
import axios from "axios";
export default function UploadPhoto(){
    const [image , setImage] = useState(null);
    
    const handleClick = (events)=>{
        events.preventDefault();
        const formData = new FormData();
        console.log(image);
        formData.append('image', image);
        axios.post("http://localhost:8081/api/photos/new",formData, {withCredentials:true})
        .then((res) => console.log(res))
        .catch((er)=>console.log(er));
    }
    const handleChange = (event)=>{
        setImage(event.target.files[0]);
    }
    return (
        <div>
            <input type="file" title = "UploadPhoto" name="UploadPhoto" onChange={handleChange}/>
            <Button variant='contained'color="success" onClick={handleClick} >Upload</Button>
        </div>
    )
}
