import React, { useState, useEffect } from 'react';
import {
  AppBar, Toolbar, Typography
} from '@mui/material';
import axios from 'axios';
import { Link } from 'react-router-dom';

const TopBar = () => {
  const [id, setId] = useState(0);
  const [userInfo, setUserInfo] = useState({});
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loggedInUserInfo, setLoggedInUserInfo] = useState({});


  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const response = await axios.get("http://localhost:8081/admin/loginUser", {withCredentials: true});
        const loggedInUserInfoModel = {
          _id: response.data._id,
          first_name: response.data.first_name,
          last_name: response.data.last_name
        };
        setLoggedInUserInfo(loggedInUserInfoModel);
        setIsLoggedIn(true);
      } catch (err) {
        console.log(err);
      }
    };

    fetchUserInfo();
  }, []);

  useEffect(() => {
    const userId = getUserId();
    if (userId) {
      const fetchUserDetail = async () => {
        try {
          const response = await axios.get(`http://localhost:8081/api/user/${userId}`, {withCredentials: true});
          if (userId !== id) {
            const userInfoModel = {
              _id: response.data._id,
              first_name: response.data.first_name,
              last_name: response.data.last_name,
              location: response.data.location,
              description: response.data.description,
              occupation: response.data.occupation
            };
            setUserInfo(userInfoModel);
            setId(userId);
          }
        } catch (err) {
          console.log(err);
        }
      };

      fetchUserDetail();
    }
  }, [id]);

  const getUserId = () => {
    const currentURL = window.location.href;
    let userId;
    if (currentURL.includes("/users/")) {
      userId = currentURL.substring(currentURL.indexOf("/users/") + "/users/".length);
    } else if (currentURL.includes("/photos/")) {
      userId = currentURL.substring(currentURL.indexOf("/photos/") + "/photos/".length);
    }
    return userId;
  };

  const loginStatus = () => {
    const elements = [];
    if (isLoggedIn) {
      elements.push("Hi, " + loggedInUserInfo.first_name + "  ");
      elements.push(
        <a href="/addPhotos" className="addPhotoLink" key={1}>
          Add Photo
        </a>
      );
      elements.push("  ");
      elements.push("  ");
      elements.push(
        <a href="#" onClick={(e) => handleLogout(e)} className="logoutLink" key={3}>
          Logout
        </a>
      );
    }
    return elements;
  };

  const title = () => {
    let title = "";
    if (isLoggedIn) {
      const currentURL = window.location.href;
      let userObj;
      if (currentURL.includes("/users/")) {
        userObj = userInfo;
        title = userObj ? `${userObj.first_name} ${userObj.last_name}` : "";
      } else if (currentURL.includes("/photos/")) {
        userObj = userInfo;
        title = userObj ? `Photos of ${userObj.first_name} ${userObj.last_name}` : "";
      }
    } else {
      return(
        <Link to="/login">Please Login</Link>
      )
      return(
        {title}
      )
    }
  };

  const handleLogout = (event) => {
    event.preventDefault();
    if (isLoggedIn) {
      axios.post("/admin/logout", {})
        .then((response) => {
          console.log(response);
          window.location.assign("/photo-share.html#/login-register");
          window.location.reload();
          sessionStorage.clear();
          console.log('Session storage is cleared!');
          setIsLoggedIn(false);
        })
        .catch((err) => {
          console.log(err);
        });
    }
  };

  return (
    <AppBar className="cs142-topbar-appBar" position="absolute">
      <Toolbar>
        <Typography>
          Photo Sharing App
        </Typography>
        <Typography>
          {createTitle()}
          <br/>
          <Typography>
            {createLoginStatus()}
          </Typography>
        </Typography>
      </Toolbar>
    </AppBar>
  );
};

export default TopBar;
