import { Box, Button, TextField } from "@mui/material"
import { useState } from "react";
import axios from "axios";
function AddCommentBox(props){
    const [comment, setComment] = useState("");
    const handleClick = (event)=>{
        axios.post(`http://localhost:8081/api/photo/${props.photoId}`,{photoId: props.photoId,userId: props.userId, comment:comment},{withCredentials:true});
    }
    const handleChange = (event)=>{
        setComment(event.target.value);
    }
    return(
        <Box>
            <TextField value={comment} onChange={handleChange}/>
            <Button onClick={handleClick}>Submit</Button>
        </Box>
        
    );
}
export default AddCommentBox;
